import React, { useState } from 'react';
import axios from 'axios';

function FileUpload() {
  const [file, setFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [uploadStatus, setUploadStatus] = useState('');

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    setPreviewUrl(URL.createObjectURL(selectedFile));  // Preview before upload
  };

  const handleFileUpload = () => {
    if (!file) {
      setUploadStatus('Please select a file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    // Update with your API URL where the file should be uploaded
    axios.post('http://localhost:5000/upload', formData)
      .then(response => {
        setUploadStatus('File uploaded successfully!');
        console.log(response.data);  // Handle response data
      })
      .catch(error => {
        setUploadStatus('File upload failed');
        console.error(error);
      });
  };

  return (
    <div>
      <h2>File Upload</h2>
      <input type="file" onChange={handleFileChange} />
      {previewUrl && <div>
        <h3>Preview:</h3>
        <img src={`http://localhost:5000/uploads/${file.name}`} alt="Preview" style={{ width: '100px', height: '100px' }} />
      </div>}
      <button onClick={handleFileUpload}>Upload File</button>
      <p>{uploadStatus}</p>
    </div>
  );
}

export default FileUpload;
